import styled from 'styled-components';


export const CollectionItemContainer = styled.div`
    width: 22vw;
    display: flex;
    flex-direction: column;
    height: 350px;
    align-items: center;
    position: relative;

    &:hover {
        .image {
            opacity: .85;
        }

        button {
            opacity: .85;
            display: flex;
        }
    }
`;

export const ImageContainer = styled.img`
    width: 100%;
    height: 95%;
    background-size: cover;
    background-position: center;
    margin-bottom: 5px;
    background
`;
