import styled from 'styled-components';


export const MenuItemContainer = styled.div`
    min-width: 30%;
    height: 240px;
    flex: 1 1 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    border: 1px solid black;
    margin: 0 7.5px 15px;
    overflow: hidden;

    &:hover {
      cursor: pointer;
    }
`;

export const BackgroundImageContainer = styled.div`
    backgroud-image: ({})
`;
