import React from 'react';
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import {
    CheckoutContainer,
    HeaderContainer,
    HeaderBlockContainer
} from './checkout.styles';

import { selectCartItems, selectorCartTotal } from '../../redux/cart/cart.selectors'
import CheckoutItem from '../../components/checkout-item/checkout-item.component';
import StripeButton from '../../components/stripe-button/stripe-button.component';

const Checkout = ({ cartItems, total }) => (
    <CheckoutContainer>
        <HeaderContainer>
            <HeaderBlockContainer>
                <span>Product</span>
            </HeaderBlockContainer>
            <HeaderBlockContainer className='header-block'>
                <span>Description</span>
            </HeaderBlockContainer>
            <HeaderBlockContainer>
                <span>Quantity</span>
            </HeaderBlockContainer>
            <HeaderBlockContainer>
                <span>Price</span>
            </HeaderBlockContainer>
            <HeaderBlockContainer>
                <span>Remove</span>
            </HeaderBlockContainer>
        </HeaderContainer>
        {cartItems.map(item => (
            <CheckoutItem
                key={item.id}
                item={item}
            />
        ))}
        <div className='total'>
            <span>TOTAL: ${total}</span>
            <div className='test-warning'>
                *Please use the following credic card for payments*
                <br />
                4242 4242 4242 4242 - Exp: 01/{(new Date().getFullYear() + 1).toString().substr(-2)} - CVV: 123
            </div>
            <StripeButton price={total} />
        </div>
    </CheckoutContainer>
);

const mapStateToProps = createStructuredSelector({
    cartItems: selectCartItems,
    total: selectorCartTotal
});

export default connect(mapStateToProps)(Checkout);
